/**
 * Customer account controller
 *
 * @category   Mage
 * @package    Mage_Customer
 * @author      Magento Core Team <core@magentocommerce.com>
 */
 require_once 'Mage/Customer/controllers/AccountController.php';
class Plumtree_Advancecustomer_AccountController extends Mage_Customer_AccountController
{
	    protected function _loginPostRedirect()
    {
		$flag=false;
        $session = $this->_getSession();

        if (!$session->getBeforeAuthUrl() || $session->getBeforeAuthUrl() == Mage::getBaseUrl()) {
            // Set default URL to redirect customer to
			
	 $session->setBeforeAuthUrl(Mage::helper('customer')->getAccountUrl());
            // Redirect customer to the last page visited after logging in
            if ($session->isLoggedIn()) {
                if (!Mage::getStoreConfigFlag(
                    Mage_Customer_Helper_Data::XML_PATH_CUSTOMER_STARTUP_REDIRECT_TO_DASHBOARD
                )) {
                    $referer = $this->getRequest()->getParam(Mage_Customer_Helper_Data::REFERER_QUERY_PARAM_NAME);
                    if ($referer) {
                        // Rebuild referer URL to handle the case when SID was changed
                        $referer = Mage::getModel('core/url')
                            ->getRebuiltUrl(Mage::helper('core')->urlDecode($referer));
                        if ($this->_isUrlInternal($referer)) {
                            $session->setBeforeAuthUrl($referer);
                        }
                    }
                } else if ($session->getAfterAuthUrl()) {
                    $session->setBeforeAuthUrl($session->getAfterAuthUrl(true));
                }
            } else {
                $session->setBeforeAuthUrl(Mage::helper('customer')->getLoginUrl());
            }
        } else if ($session->getBeforeAuthUrl() == Mage::helper('customer')->getLogoutUrl()) {
            $session->setBeforeAuthUrl(Mage::helper('customer')->getDashboardUrl());
        } else {
					$customerportaldata = Mage::getModel('customerportal/customerportal')->getCollection();
			$groupId = Mage::getSingleton('customer/session')->getCustomerGroupId(); 
			foreach($customerportaldata as $customerportaldata):
				if($groupId==$customerportaldata->getData('customer_group'))
				{
					 	$session->setCustomerPortalId($customerportaldata->getData('customerportal_id'));
						$cstomerportalurl=Mage::getBaseUrl().'customerportal';
					    $session->setBeforeAuthUrl($cstomerportalurl);
						$flag=true;						
				}
			endforeach;
			if($flag==false)
			{
            $session->setBeforeAuthUrl(Mage::helper('customer')->getAccountUrl());
			}
            if (!$session->getAfterAuthUrl()) {
                $session->setAfterAuthUrl($session->getBeforeAuthUrl());
            }
            if ($session->isLoggedIn()) {
                $session->setBeforeAuthUrl($session->getAfterAuthUrl(true));
            }
        }
        $this->_redirectUrl($session->getBeforeAuthUrl(true));
    }
}
